tool.command:

  ln -f -s ${input_cpin_path} ${input_cpin_path}_`echo $$`.${input_cpin_path.ext};

  ln -f -s ${input_cpout_path} ${input_cpout_path}_`echo $$`.${input_cpout_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

cphstats_run

#if $config.sele == "option1":
    --config ${config.properties}_`echo $$`.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__timestep__dq__:${properties.timestep},__dq__verbose__dq__:${properties.verbose},__dq__interval__dq__:${properties.interval},__dq__protonated__dq__:${properties.protonated},__dq__pka__dq__:${properties.pka},__dq__calcpka__dq__:${properties.calcpka},__dq__running_avg_window__dq__:${properties.running_avg_window},__dq__chunk_window__dq__:${properties.chunk_window},__dq__cumulative__dq__:${properties.cumulative},__dq__fix_remd__dq__:__dq__${properties.fix_remd}__dq__,__dq__conditional__dq__:__dq__${properties.conditional}__dq__,__dq__binary_path__dq__:__dq__${properties.binary_path}__dq____cc__'
  #end if

  #if str($input_cpin_path) != 'None':
    --input_cpin_path ${input_cpin_path}_`echo $$`.${input_cpin_path.ext}
  #end if

  #if str($input_cpout_path) != 'None':
    --input_cpout_path ${input_cpout_path}_`echo $$`.${input_cpout_path.ext}
  #end if


  --output_dat_path $outname_output_dat_path

  --output_population_path $outname_output_population_path

  --output_chunk_path $outname_output_chunk_path

  --output_cumulative_path $outname_output_cumulative_path

  --output_conditional_path $outname_output_conditional_path

  --output_chunk_conditional_path $outname_output_chunk_conditional_path
;
#if $config.sele == "option1":
    rm -f ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

  rm -f ${input_cpin_path}_`echo $$`.${input_cpin_path.ext};

  rm -f ${input_cpout_path}_`echo $$`.${input_cpout_path.ext};


  if test -f $outname_output_dat_path; then mv $outname_output_dat_path $output_dat_path; fi;

  if test -f $outname_output_population_path; then mv $outname_output_population_path $output_population_path; fi;

  if test -f $outname_output_chunk_path; then mv $outname_output_chunk_path $output_chunk_path; fi;

  if test -f $outname_output_cumulative_path; then mv $outname_output_cumulative_path $output_cumulative_path; fi;

  if test -f $outname_output_conditional_path; then mv $outname_output_conditional_path $output_conditional_path; fi;

  if test -f $outname_output_chunk_conditional_path; then mv $outname_output_chunk_conditional_path $output_chunk_conditional_path; fi;
