tool.command:

ln -f -s ${input_traj_path} ${input_traj_path}.${input_traj_path.ext};

ln -f -s ${input_index_path} ${input_index_path}.${input_index_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_analysis:3.0.0--py_0 gmx_trjconv_trj
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#else if $config.sele == "option4":
--config "__oc____dq__selection__dq__:__dq__${config.selection}__dq__,__dq__start__dq__:__dq__${config.start}__dq__,__dq__end__dq__:__dq__${config.end}__dq__,__dq__dt__dq__:__dq__${config.dt}__dq__,__dq__gmx_path__dq__:__dq__${config.gmx_path}__dq____cc__"
#end if

--input_traj_path ${input_traj_path}.${input_traj_path.ext}

--input_index_path ${input_index_path}.${input_index_path.ext}


--output_traj_path $__root_dir__/database/files/000/$outname_output_traj_path
;          
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_traj_path}.${input_traj_path.ext};

rm -f ${input_index_path}.${input_index_path.ext};


if test -f $__root_dir__/database/files/000/$outname_output_traj_path; then mv $__root_dir__/database/files/000/$outname_output_traj_path $output_traj_path; fi;
