tool.command:

  ln -f -s ${input_pcz_path} ${input_pcz_path}_`echo $$`.${input_pcz_path.ext};

#if $config.sele == "option1":
    ln -s -f ${config.properties} ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

pcz_unzip

#if $config.sele == "option1":
    --config ${config.properties}_`echo $$`.${config.properties.ext}
  #else if $config.sele == "option2":
    --config '${config.jsonstr}'
  #else if $config.sele == "option3":
    --config '__oc____dq__binary_path__dq__:__dq__${properties.binary_path}__dq__,__dq__verbose__dq__:${properties.verbose},__dq__pdb__dq__:${properties.pdb}__cc__'
  #end if

  #if str($input_pcz_path) != 'None':
    --input_pcz_path ${input_pcz_path}_`echo $$`.${input_pcz_path.ext}
  #end if


  --output_crd_path $outname_output_crd_path
;
#if $config.sele == "option1":
    rm -f ${config.properties}_`echo $$`.${config.properties.ext};
  #end if

  rm -f ${input_pcz_path}_`echo $$`.${input_pcz_path.ext};


