tool.command:

  ln -f -s ${input_decode_path} ${input_decode_path}.${input_decode_path.ext};

  ln -f -s ${input_predict_path} ${input_predict_path}.${input_predict_path.ext};

#if $config.sele == "option1":
  ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_ml:3.0.1--py_0 autoencoder_neural_network
#if $config.sele == "option1":
  --config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
  --config ${config.jsonstr}
#else if $config.sele == "option4":
  --config "__oc____dq__optimizer__dq__:__dq__${config.optimizer}__dq__,__dq__learning_rate__dq__:__dq__${config.learning_rate}__dq__,__dq__batch_size__dq__:__dq__${config.batch_size}__dq__,__dq__max_epochs__dq__:__dq__${config.max_epochs}__dq____cc__"
#end if

  --input_decode_path ${input_decode_path}.${input_decode_path.ext}

  --input_predict_path ${input_predict_path}.${input_predict_path.ext}


  --output_model_path $__root_dir__/database/files/000/$outname_output_model_path

  --output_test_decode_path $__root_dir__/database/files/000/$outname_output_test_decode_path

  --output_test_predict_path $__root_dir__/database/files/000/$outname_output_test_predict_path
;          
#if $config.sele == "option1":
  rm -f ${config.properties}.${config.properties.ext};
#end if

  rm -f ${input_decode_path}.${input_decode_path.ext};

  rm -f ${input_predict_path}.${input_predict_path.ext};


  if test -f $__root_dir__/database/files/000/$outname_output_model_path; then mv $__root_dir__/database/files/000/$outname_output_model_path $output_model_path; fi;

  if test -f $__root_dir__/database/files/000/$outname_output_test_decode_path; then mv $__root_dir__/database/files/000/$outname_output_test_decode_path $output_test_decode_path; fi;

  if test -f $__root_dir__/database/files/000/$outname_output_test_predict_path; then mv $__root_dir__/database/files/000/$outname_output_test_predict_path $output_test_predict_path; fi;
