tool.command:

ln -f -s ${input_decode_path} ${input_decode_path}.${input_decode_path.ext};

ln -f -s ${input_model_path} ${input_model_path}.${input_model_path.ext};

#if $config.sele == "option1":
ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_ml:3.0.1--py_0 neural_network_decode
#if $config.sele == "option1":
--config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
--config ${config.jsonstr}
#else if $config.sele == "option4":
--config "__oc____cc__"
#end if

--input_decode_path ${input_decode_path}.${input_decode_path.ext}

--input_model_path ${input_model_path}.${input_model_path.ext}


--output_decode_path $__root_dir__/database/files/000/$outname_output_decode_path

--output_predict_path $__root_dir__/database/files/000/$outname_output_predict_path
;          
#if $config.sele == "option1":
rm -f ${config.properties}.${config.properties.ext};
#end if

rm -f ${input_decode_path}.${input_decode_path.ext};

rm -f ${input_model_path}.${input_model_path.ext};


if test -f $__root_dir__/database/files/000/$outname_output_decode_path; then mv $__root_dir__/database/files/000/$outname_output_decode_path $output_decode_path; fi;

if test -f $__root_dir__/database/files/000/$outname_output_predict_path; then mv $__root_dir__/database/files/000/$outname_output_predict_path $output_predict_path; fi;
