tool.command:

  ln -f -s ${input_top_path} ${input_top_path}.${input_top_path.ext};

  ln -f -s ${input_traj_path} ${input_traj_path}.${input_traj_path.ext};

  ln -f -s ${input_exp_path} ${input_exp_path}.${input_exp_path.ext};

#if $config.sele == "option1":
  ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_analysis:3.0.0--py_0 cpptraj_rms
#if $config.sele == "option1":
  --config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
  --config '${config.jsonstr}'
#else if $config.sele == "option4":
  --config "__oc____dq__in_parameters__dq__:__dq__${config.in_parameters}__dq__,__dq__cpptraj_path__dq__:__dq__${config.cpptraj_path}__dq__,__dq__remove_tmp__dq__:__dq__${config.remove_tmp}__dq__,__dq__restart__dq__:__dq__${config.restart}__dq____cc__"
#end if

  --input_top_path ${input_top_path}.${input_top_path.ext}

  --input_traj_path ${input_traj_path}.${input_traj_path.ext}

  --input_exp_path ${input_exp_path}.${input_exp_path.ext}


  --output_cpptraj_path $__root_dir__/database/files/000/$outname_output_cpptraj_path
;          
#if $config.sele == "option1":
  rm -f ${config.properties}.${config.properties.ext};
#end if

  rm -f ${input_top_path}.${input_top_path.ext};

  rm -f ${input_traj_path}.${input_traj_path.ext};

  rm -f ${input_exp_path}.${input_exp_path.ext};


  if test -f $__root_dir__/database/files/000/$outname_output_cpptraj_path; then mv $__root_dir__/database/files/000/$outname_output_cpptraj_path $output_cpptraj_path; fi;
