tool.command:

  ln -f -s ${input_structure_path} ${input_structure_path}.${input_structure_path.ext};

#if $config.sele == "option1":
  ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_structure_utils:3.0.0--py_0 renumber_structure
#if $config.sele == "option1":
  --config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
  --config ${config.jsonstr}
#else if $config.sele == "option4":
  --config "__oc____dq__renumber_residues__dq__:__dq__${config.renumber_residues}__dq__,__dq__renumber_residues_per_chain__dq__:__dq__${config.renumber_residues_per_chain}__dq____cc__"
#end if

  --input_structure_path ${input_structure_path}.${input_structure_path.ext}


  --output_structure_path $__root_dir__/database/files/000/$outname_output_structure_path

  --output_mapping_json_path $__root_dir__/database/files/000/$outname_output_mapping_json_path
;          
#if $config.sele == "option1":
  rm -f ${config.properties}.${config.properties.ext};
#end if

  rm -f ${input_structure_path}.${input_structure_path.ext};


  if test -f $__root_dir__/database/files/000/$outname_output_structure_path; then mv $__root_dir__/database/files/000/$outname_output_structure_path $output_structure_path; fi;

  if test -f $__root_dir__/database/files/000/$outname_output_mapping_json_path; then mv $__root_dir__/database/files/000/$outname_output_mapping_json_path $output_mapping_json_path; fi;
