tool.command:

  ln -f -s ${input_pdb_path} ${input_pdb_path}.${input_pdb_path.ext};

  ln -f -s ${resid_pdb_path} ${resid_pdb_path}.${resid_pdb_path.ext};

#if $config.sele == "option1":
  ln -s -f ${config.properties} ${config.properties}.${config.properties.ext};
#end if
docker run --rm -v $__root_dir__/database/files:$__root_dir__/database/files quay.io/biocontainers/biobb_vs:3.0.0--py_0 box
#if $config.sele == "option1":
  --config ${config.properties}.${config.properties.ext}
#else if $config.sele == "option2":
  --config '${config.jsonstr}'
#else if $config.sele == "option4":
  --config "__oc____dq__offset__dq__:__dq__${config.offset}__dq__,__dq__residue_offset__dq__:__dq__${config.residue_offset}__dq__,__dq__box_coordinates__dq__:__dq__${config.box_coordinates}__dq__,__dq__remove_tmp__dq__:__dq__${config.remove_tmp}__dq__,__dq__restart__dq__:__dq__${config.restart}__dq____cc__"
#end if

  --input_pdb_path ${input_pdb_path}.${input_pdb_path.ext}

  --resid_pdb_path ${resid_pdb_path}.${resid_pdb_path.ext}


  --output_pdb_path $__root_dir__/database/files/000/$outname_output_pdb_path
;          
#if $config.sele == "option1":
  rm -f ${config.properties}.${config.properties.ext};
#end if

  rm -f ${input_pdb_path}.${input_pdb_path.ext};

  rm -f ${resid_pdb_path}.${resid_pdb_path.ext};


  if test -f $__root_dir__/database/files/000/$outname_output_pdb_path; then mv $__root_dir__/database/files/000/$outname_output_pdb_path $output_pdb_path; fi;
